import { useEffect, useState } from 'react';

const WEATHER_ENDPOINT_URI = 'https://api.weather.gov/gridpoints/SGX/55,22/forecast/hourly?units=us';

const WeatherWidget = () => {
  const [weatherData, setWeatherData] = useState(null);

  useEffect(() => {
    fetch(WEATHER_ENDPOINT_URI)
      .then((res) => res.json())
      .then((body) => 
        setWeatherData(body.properties.periods[0])
      )
      .catch(() => null);
  }, []);

  if (weatherData == null) return null;

  const {
    temperature,
    temperatureUnit,
    shortForecast,
    windSpeed,
    windDirection,
    relativeHumidity,
    probabilityOfPrecipitation,
  } = weatherData;

  const curDateHours = new Date().getHours();
  this.dataset.theme = shortForecast.toLowerCase().includes('sunny')
    ? 'sunny'
    : curDateHours > 8 && curDateHours < 20
      ? 'cloudy'
      : 'dark';

  return (
    <section data-theme={ null }>
      <ul>
        <li>{ temperature }Â°{ temperatureUnit }</li>
        <li>{ shortForecast }</li>
        <li>Wind: { windSpeed } { windDirection }</li>
        <li>Humidity: { relativeHumidity.value }%</li>
        <li>Chance of rain (this hour): { probabilityOfPrecipitation.value }%</li>
      </ul>
    </section>
  );
};

export default WeatherWidget;
