import { useEffect, useState } from 'react';

const WEATHER_ENDPOINT_URI = 'https://api.weather.gov/gridpoints/SGX/55,22/forecast/hourly?units=us';

const WeatherWidget = () => {
  const [weatherData, setWeatherData] = useState(null);

  useEffect(() => {
    fetch(WEATHER_ENDPOINT_URI)
      .then((res) => res.json())
      .then((body) => 
        setWeatherData(body.properties.periods[0])
      )
      .catch(() => null);
  }, []);

  if (weatherData == null) return null;

  const value = weatherData.temperature;

  return (
    <section data-theme={ value }>
      <ul>
        <li>{ weatherData.temperature }Â°{ weatherData.temperatureUnit }</li>
        <li>{ weatherData.shortForecast }</li>
        <li>Wind: { weatherData.windSpeed } { weatherData.windDirection }</li>
        <li>Humidity: { weatherData.relativeHumidity.value }%</li>
        <li>Chance of rain (this hour): { weatherData.probabilityOfPrecipitation.value }%</li>
      </ul>
    </section>
  );
};

export default WeatherWidget;
