import { useEffect, useState } from 'react';

const WEATHER_ENDPOINT_URI = 'https://api.weather.gov/gridpoints/SGX/55,22/forecast/hourly?units=us';

const WeatherWidget = () => {
  const [weatherData, setWeatherData] = useState(null);

  useEffect(() => {
    fetch(WEATHER_ENDPOINT_URI)
      .then((res) => res.json())
      .then((body) => 
        setWeatherData(body.properties.periods[0])
      )
      .catch(() => null);
  }, []);

  return (
    <section data-theme={ null }>
      <ul>
        <li>{ weatherData.temperature }</li>
        <li>{ null }</li>
        <li>{ null }</li>
        <li>{ null }</li>
        <li>{ null }</li>
      </ul>
    </section>
  );
};

export default WeatherWidget;
