import { useState, useRef } from 'react';

const NUM_STARS = 5;
const STAR_ICON = '★';
const POS_MESSAGE_THRESHOLD = 0.8;

const RatingWidget = () => {
  const [submittedForm, setSubmittedForm] = useState(false);
  const [] = useRef(null);

  const handleClick = (starRating) => {
    setSubmittedForm(true);

    const formBody = new FormData();
    formBody.append('rating', starRating);
    formBody.append('question', this._questionText);
    formBody.append('sentBy', 'JS');

    fetch('https://httpbin.org/post', {
        method: 'POST',
        headers: {
          'X-Sent-By': 'JavaScript',
        },
        body: formBody,
      })
      .then((res) => res.json())
      .then(console.log); 
  }

  const confirmationText = (starRating / this._numStars) >= POS_MESSAGE_THRESHOLD
  ? `Thanks for the ${ starRating } star rating!`
  : `Thanks for the feedback of ${ starRating } stars. We'll try to do better!`;

  return (
    <section className='rating-widget'>
      {
        submittedForm
        ?
          <p>{ confirmationText }</p>
        : 
          Array.from({ length: NUM_STARS })
            .map((_, i) =>
              <button
                className='star-button'
                key={ i }
                onClick={ () => handleClick(i + 1) }
              >
                { STAR_ICON }
              </button>
            )
      }
    </section>
  );
};

export default RatingWidget;
